import React, { useState } from 'react';
import { fetchHistory, postNickname } from './api';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import AboutPage from './AboutPage';
import ContactPage from './ContactPage';
import HistoryPage from './HistoryPage';
import LoginPage from './LoginPage'; // Импортируйте LoginPage
import './App.css';
import { Footer } from "./components/Footer/Footer";
import { Header } from "./components/Header/Header";

function App() {
  const [nickname, setNickname] = useState('');
  const [history, setHistory] = useState([]);
  // Добавьте состояние для авторизации
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleNicknameSubmit = async (event) => {
    event.preventDefault();
    const result = await postNickname(nickname);
    console.log(result); // Обработайте результат как необходимо
  };

  const handleFetchHistory = async () => {
    const historyData = await fetchHistory();
    setHistory(historyData);
  };

  // Добавьте функцию для обработки логина
  const handleLogin = (username, password) => {
    // Здесь должна быть логика авторизации
    console.log('Попытка входа с именем пользователя:', username);
    // Предположим, что пользователь успешно авторизован
    setIsAuthenticated(true);
  };

  return (
    <BrowserRouter>
      <div className="App">
        <Header />
        {/* Добавьте навигацию с условием отображения в зависимости от состояния авторизации */}
        <nav>
          {isAuthenticated ? (
            <Link to="/history">История запросов</Link>
          ) : (
            <Link to="/login">Авторизация</Link>
          )}
        </nav>
        <Routes>
          <Route path="/" element={
            <>
              <form onSubmit={handleNicknameSubmit}>
                <input
                  type="text"
                  value={nickname}
                  onChange={(e) => setNickname(e.target.value)}
                  placeholder="Введите никнейм"
                />
                <button type="submit">Отправить</button>
              </form>
              {isAuthenticated && (
                <button onClick={handleFetchHistory}>Получить историю</button>
              )}
              {/* Условие для отображения ссылки на историю */}
            </>
          } />
          <Route path="/about" element={<AboutPage />} />
          <Route path="/contacts" element={<ContactPage />} />
          <Route path="/history" element={<HistoryPage history={history} />} />
          {/* Добавьте маршрут для LoginPage */}
          <Route path="/login" element={<LoginPage onLogin={handleLogin} />} />
        </Routes>
        <Footer />
      </div>
    </BrowserRouter>
  );
}

export default App;
