import axios from 'axios';

const API_BASE_URL = 'http://localhost:3000'; // Замените на URL вашего сервера FastAPI

export const fetchHistory = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/history`);
    return response.data;
  } catch (error) {
    console.error('Ошибка при получении истории запросов:', error);
  }
};

export const postNickname = async (nickname) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/`, { nickname });
    return response.data;
  } catch (error) {
    console.error('Ошибка при отправке никнейма:', error);
  }
};
