import React, { useState } from 'react';
import { fetchHistory, postNickname } from './api';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import AboutPage from './AboutPage';
import ContactPage from './ContactPage';
import HistoryPage from './HistoryPage'; // Импортируйте HistoryPage
import './App.css';
import { Footer } from "./components/Footer/Footer";
import { Header } from "./components/Header/Header";

function App() {
  const [nickname, setNickname] = useState('');
  const [history, setHistory] = useState([]);

  const handleNicknameSubmit = async (event) => {
    event.preventDefault();
    const result = await postNickname(nickname);
    console.log(result); // Обработайте результат как необходимо
  };

  const handleFetchHistory = async () => {
    const historyData = await fetchHistory();
    setHistory(historyData);
  };

  return (
    <BrowserRouter>
      <div className="App">
        <Header />
        <Routes>
          <Route path="/" element={
            <>
              <form onSubmit={handleNicknameSubmit}>
                <input
                  type="text"
                  value={nickname}
                  onChange={(e) => setNickname(e.target.value)}
                  placeholder="Введите никнейм"
                />
                <button type="submit">Отправить</button>
              </form>
              <button onClick={handleFetchHistory}>Получить историю</button>
              {/* Добавьте ссылку на страницу истории */}
              <Link to="/history">История запросов</Link>
            </>
          } />
          <Route path="/about" element={<AboutPage />} />
          <Route path="/contacts" element={<ContactPage />} />
          {/* Добавьте маршрут для HistoryPage */}
          <Route path="/history" element={<HistoryPage history={history} />} />
        </Routes>
        <Footer />
      </div>
    </BrowserRouter>
  );
}

export default App;
